bool Carts::validJsonOfField(size_t index,
                             const std::string &fieldName,
                             const Json::Value &pJson,
                             std::string &err,
                             bool isForCreation)
{
    switch(index)
    {
        case 0:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isString())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 1:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isString())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 2:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isString())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 3:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isNumeric())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 4:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isInt())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 5:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isString())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        case 6:
            if(pJson.isNull())
            {
                err="The " + fieldName + " column cannot be null";
                return false;
            }
            if(!pJson.isString())
            {
                err="Type error in the "+fieldName+" field";
                return false;
            }
            break;
        default:
            err="Internal error in the server";
            return false;
    }
    return true;
}
MasterCustomers Carts::getMaster_customers(const DbClientPtr &clientPtr) const {
    static const std::string sql = "select * from master_customers where id = $1";
    Result r(nullptr);
    {
        auto binder = *clientPtr << sql;
        binder << *customerId_ << Mode::Blocking >>
            [&r](const Result &result) { r = result; };
        binder.exec();
    }
    if (r.size() == 0)
    {
        throw UnexpectedRows("0 rows found");
    }
    else if (r.size() > 1)
    {
        throw UnexpectedRows("Found more than one row");
    }
    return MasterCustomers(r[0]);
}

void Carts::getMaster_customers(const DbClientPtr &clientPtr,
                                const std::function<void(MasterCustomers)> &rcb,
                                const ExceptionCallback &ecb) const
{
    static const std::string sql = "select * from master_customers where id = $1";
    *clientPtr << sql
               << *customerId_
               >> [rcb = std::move(rcb), ecb](const Result &r){
                    if (r.size() == 0)
                    {
                        ecb(UnexpectedRows("0 rows found"));
                    }
                    else if (r.size() > 1)
                    {
                        ecb(UnexpectedRows("Found more than one row"));
                    }
                    else
                    {
                        rcb(MasterCustomers(r[0]));
                    }
               }
               >> ecb;
}
std::vector<MasterProducts> Carts::getMaster_products(const DbClientPtr &clientPtr) const {
    static const std::string sql = "select * from master_products where id = $1";
    Result r(nullptr);
    {
        auto binder = *clientPtr << sql;
        binder << *productId_ << Mode::Blocking >>
            [&r](const Result &result) { r = result; };
        binder.exec();
    }
    std::vector<MasterProducts> ret;
    ret.reserve(r.size());
    for (auto const &row : r)
    {
        ret.emplace_back(MasterProducts(row));
    }
    return ret;
}

void Carts::getMaster_products(const DbClientPtr &clientPtr,
                               const std::function<void(std::vector<MasterProducts>)> &rcb,
                               const ExceptionCallback &ecb) const
{
    static const std::string sql = "select * from master_products where id = $1";
    *clientPtr << sql
               << *productId_
               >> [rcb = std::move(rcb)](const Result &r){
                   std::vector<MasterProducts> ret;
                   ret.reserve(r.size());
                   for (auto const &row : r)
                   {
                       ret.emplace_back(MasterProducts(row));
                   }
                   rcb(ret);
               }
               >> ecb;
}
