std::vector<Carts> MasterCustomers::getCarts(const DbClientPtr &clientPtr) const {
    static const std::string sql = "select * from carts where customer_id = $1";
    Result r(nullptr);
    {
        auto binder = *clientPtr << sql;
        binder << *id_ << Mode::Blocking >>
            [&r](const Result &result) { r = result; };
        binder.exec();
    }
    std::vector<Carts> ret;
    ret.reserve(r.size());
    for (auto const &row : r)
    {
        ret.emplace_back(Carts(row));
    }
    return ret;
}

void MasterCustomers::getCarts(const DbClientPtr &clientPtr,
                               const std::function<void(std::vector<Carts>)> &rcb,
                               const ExceptionCallback &ecb) const
{
    static const std::string sql = "select * from carts where customer_id = $1";
    *clientPtr << sql
               << *id_
               >> [rcb = std::move(rcb)](const Result &r){
                   std::vector<Carts> ret;
                   ret.reserve(r.size());
                   for (auto const &row : r)
                   {
                       ret.emplace_back(Carts(row));
                   }
                   rcb(ret);
               }
               >> ecb;
}
